-- ============================================================================
-- ROLE PERMISSIONS MAPPING
-- ============================================================================

-- Guest permissions
INSERT INTO role_permissions (role, permission_id, granted) 
SELECT 'guest', id, true FROM permissions WHERE name IN (
    'listing.read',
    'review.read'
);

-- Customer permissions
INSERT INTO role_permissions (role, permission_id, granted) 
SELECT 'customer', id, true FROM permissions WHERE name IN (
    'user.read',
    'user.update',
    'listing.read',
    'booking.create',
    'booking.read',
    'booking.cancel',
    'review.create',
    'review.read',
    'customer.payment',
    'customer.bookings'
);

-- Provider permissions (includes customer permissions)
INSERT INTO role_permissions (role, permission_id, granted) 
SELECT 'provider', id, true FROM permissions WHERE name IN (
    'user.read',
    'user.update',
    'listing.create',
    'listing.read',
    'listing.update',
    'listing.delete',
    'booking.read',
    'booking.update',
    'review.create',
    'review.read',
    'provider.availability',
    'provider.analytics',
    'customer.payment',
    'customer.bookings'
);

-- Admin permissions (all permissions)
INSERT INTO role_permissions (role, permission_id, granted) 
SELECT 'admin', id, true FROM permissions;

-- Super admin permissions (all permissions)
INSERT INTO role_permissions (role, permission_id, granted) 
SELECT 'super_admin', id, true FROM permissions;

